import { createStore } from "redux";
import rootReducer from "./reducers";

//state Inicial

const initialState = {
  number: 0
};

//createStore contiene el state de la app
//3 paramenters: reducer, state, applyMiddleware
const store = createStore(rootReducer, initialState);
